// Importa las librerías necesarias y el estilo de Bootstrap si es necesario

import axios from "axios";
import { useEffect, useState } from "react";

interface Pokemon {
  name: string;
  url: string;
}

const Home = () => {
  const [pokemons, setPokemons] = useState<Pokemon[]>([]);

  useEffect(() => {
    axios
      .get("https://pokeapi.co/api/v2/pokemon?offset=0&limit=151")
      .then((response) => {
        setPokemons(response.data.results);
      })
      .catch((error) => {
        console.error("Error fetching data: ", error);
      });
  }, []);

  // Función para obtener la ruta de la imagen de un Pokémon
  const getPokemonImage = (name: string) => {
    // Aquí puedes construir la ruta de la imagen según el nombre del Pokémon
    return `https://img.pokemondb.net/sprites/omega-ruby-alpha-sapphire/dex/normal/${name.toLowerCase()}.png`;
  };

  return (
    <div>
      {pokemons.map((pokemon) => (
        <div className="Content" key={pokemon.name}>
          <img
            src={getPokemonImage(pokemon.name)}
            alt={pokemon.name}
            style={{ width: "100px", height: "100px" }}
          />
          {pokemon.name}
        </div>
      ))}
    </div>
  );
};

export default Home;
