// Home.tsx

import React, { useEffect, useState } from "react";
import axios from "axios";
import PokemonModal from "./PokemonModal"; // Ajusta la ruta según tu estructura de archivos

interface Pokemon {
  name: string;
  url: string;
}

interface PokemonDetails {
  url: string;
  number: number;
  height: number;
  weight: number;
  types: string[];
  moves: string[];
}

const Home: React.FC = () => {
  const [pokemons, setPokemons] = useState<Pokemon[]>([]);
  const [selectedPokemon, setSelectedPokemon] = useState<PokemonDetails | null>(
    null
  );

  useEffect(() => {
    axios
      .get("https://pokeapi.co/api/v2/pokemon?offset=0&limit=151")
      .then((response) => {
        setPokemons(response.data.results);
      })
      .catch((error) => {
        console.error("Error fetching data: ", error);
      });
  }, []);

  const getPokemonImage = (name: string) => {
    return `https://img.pokemondb.net/sprites/omega-ruby-alpha-sapphire/dex/normal/${name.toLowerCase()}.png`;
  };

  const handlePokemonClick = (pokemon: Pokemon) => {
    fetchPokemonDetails(pokemon.url);
  };

  const fetchPokemonDetails = async (pokemonUrl: string) => {
    try {
      const response = await axios.get(pokemonUrl);
      const details: PokemonDetails = {
        url: response.data.url,
        number: response.data.id,
        height: response.data.height,
        weight: response.data.weight,
        types: response.data.types.map((type: any) => type.type.name),
        moves: response.data.moves.map((move: any) => move.move.name),
      };
      setSelectedPokemon(details);
    } catch (error) {
      console.error("Error fetching Pokemon details: ", error);
    }
  };

  const closeModal = () => {
    setSelectedPokemon(null);
  };

  return (
    <div>
      {pokemons.map((pokemon) => (
        <div
          className="Content"
          key={pokemon.name}
          onClick={() => handlePokemonClick(pokemon)}
        >
          <img
            src={getPokemonImage(pokemon.name)}
            alt={pokemon.name}
            style={{ width: "100px", height: "100px" }}
          />
          <h3>{pokemon.name}</h3>
        </div>
      ))}

      {selectedPokemon && (
        <PokemonModal onClose={closeModal} pokemonDetails={selectedPokemon} />
      )}
    </div>
  );
};

export default Home;
