// Home.tsx

import axios from "axios";
import { useEffect, useState } from "react";
import PokemonDetails from "./PokemonDetails"; // Asegúrate de importar el componente de detalles

interface Pokemon {
  name: string;
  url: string;
}

const Home = () => {
  const [pokemons, setPokemons] = useState<Pokemon[]>([]);
  const [selectedPokemon, setSelectedPokemon] = useState<Pokemon | null>(null);

  useEffect(() => {
    axios
      .get("https://pokeapi.co/api/v2/pokemon?offset=0&limit=151")
      .then((response) => {
        setPokemons(response.data.results);
      })
      .catch((error) => {
        console.error("Error fetching data: ", error);
      });
  }, []);

  const getPokemonImage = (name: string) => {
    return `https://img.pokemondb.net/sprites/omega-ruby-alpha-sapphire/dex/normal/${name.toLowerCase()}.png`;
  };

  const handlePokemonClick = (pokemon: Pokemon) => {
    setSelectedPokemon(pokemon);
  };

  return (
    <div>
      {pokemons.map((pokemon) => (
        <div
          className="Content"
          key={pokemon.name}
          onClick={() => handlePokemonClick(pokemon)}
        >
          <img
            src={getPokemonImage(pokemon.name)}
            alt={pokemon.name}
            style={{ width: "100px", height: "100px" }}
          />
          {pokemon.name}
        </div>
      ))}

      {selectedPokemon && (
        <PokemonDetails
          pokemon={selectedPokemon}
          onClose={() => setSelectedPokemon(null)}
        />
      )}
    </div>
  );
};

export default Home;
